define('pages/userLoactions',['dataSource', 'helpers', 'elements/header'],
	function(DataSource, Helpers, Header) {

		function UsersLocationsPage(options) {
			var header = new Header(),
				self = this;

			this.defaults = {
				mapId: 'users-map',
				mapOptions: {
					center: new google.maps.LatLng(44.5403, -78.5463),
					zoom: 10,
					mapTypeId: google.maps.MapTypeId.ROADMAP
				}
			}
			header.getName(function(name) {
				self._username = name
				self.init(options);
			});
		}

		UsersLocationsPage.prototype = {

			constructor: UsersLocationsPage,

			init: function(options) {
				this.opts = Helpers.extend(options, this.defaults);

				this.userLocationsMap = document.getElementById(this.opts.mapId);

				this._map = new google.maps.Map(this.userLocationsMap, this.opts.mapOptions);
				this.addUsersMarkers();

			},

			addUsersMarkers: function() {
				var self = this;
				self.infowindow = new google.maps.InfoWindow();

				DataSource.getUsersLocations(function(resp, status) {
					if (status == 200) {
						var coords = null;
						for (var user in resp) {
							coords = new google.maps.LatLng(resp[user].geolocation.lat, resp[user].geolocation.long);
							var marker = new google.maps.Marker({
								position: coords,
								map: self._map,
								title: resp[user].name
							});
							google.maps.event.addListener(marker, 'click', function(Av) {
								self.infowindow.setContent('<div class="info-window-content">' + this.title + '</div>');
								self.infowindow.open(self._map, this);
							});
							if (self._username == resp[user].name) {
								self._map.setCenter(coords);
							}
						}
					} else {
						alert("Impossible to identify users locations");
					}
				});
			}
		}

		return UsersLocationsPage;
	}
);
define('elements/geolocation',['dataSource'],
	function(DataSource) {

		function Geolocation() {
			this.attempt = 0;
			this.getLocation();
		}

		Geolocation.prototype = {

			constructor: Geolocation,

			getLocation: function() {
				var self = this;
				self.attempt++;

				navigator.geolocation.getCurrentPosition(function(position) {
					self.attempt = 0;
					DataSource.publishLocation({
						lat: position.coords.latitude,
						long: position.coords.longitude
					}, function() {
						self.updateLocation.call(self);
					})
				}, function(err) {
					if (self.attempt < 3) {
						self.getLocation.call(self);
					} else {
						self.attempt = 0;
						self.updateLocation.call(self);
					}
				});
			},

			updateLocation: function() {
				var self = this;
				setTimeout(function() {
					self.getLocation.call(self);
				}, 1800000);
			}
		};

		return Geolocation;
	}
);
define('main',['pages/main', 'pages/privateMessages', 'pages/user','pages/userLoactions','elements/geolocation'], 
	function (MainPage, PrivateMessagesPage, UserPage, UsersLocationsPage, Geolocation) {
		var pages = {
			'main': MainPage,
			'private-messages': PrivateMessagesPage,
			'user': UserPage,
			'locations': UsersLocationsPage
		};

		var body = document.querySelector('body'),
			bodyClass = body.className,
			currentPage = null;

		for ( var page in pages ) {
			if ( bodyClass.indexOf(page) + 1 ) {
				currentPage = pages[page];
				break;
			}
		}
		if ( !currentPage ) {
			return;
		}

		var page = new currentPage(),
			geo = new Geolocation();
	}
);
